exports.Player = function(id, pseudo, idReseauxSociaux) {
    this.id = id;
    this.pseudo = pseudo;
    this.idReseauxSociaux = idReseauxSociaux;
};

exports.Player.prototype.calculRatio = function () {
    return (this.nbVictory || 0) / (this.nbDefeat || 1);
};

exports.Player.prototype.isBan = function () {
    return (this.ban != undefined);
};

exports.Player.prototype.isInGame = function () {
    return (this.game != undefined);
};

